const transcripcion = $('HTTP Request - Transcriber').first().json.transcript;
const actaReunion = $input.first().json.response;
const filename = $('HTTP Request - Transcriber').first().json.filename || 'audio';

const resultado = {
  mensaje: '✅ Transcripción completada y acta de reunión generada',
  filename: filename,
  transcripcion: transcripcion,
  acta_reunion: actaReunion,
  timestamp: new Date().toISOString()
};

// Convert JSON to binary for file writing
const jsonString = JSON.stringify(resultado, null, 2);
const binaryData = Buffer.from(jsonString, 'utf-8');

return [{
  json: resultado,
  binary: {
    data: await this.helpers.prepareBinaryData(binaryData, `${filename.replace(/\.[^/.]+$/, '')}_acta.json`, 'application/json')
  }
}];

// Return the final response (the file has been saved)
const resultado = $('Code - Resultado').first().json;
const filePath = $input.first().json.fileName;

return [{
  json: {
    ...resultado,
    archivoGuardado: filePath
  }
}];
